    @SuppressWarnings("empty-statement")
    private void changeActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_changeActionPerformed
       int am,to,lo,qua,di,ni,pen,sum;
       float amo,sum0;
       amo=Float.parseFloat(amount.getText());
       sum0=amo;
       am=(int) Math.floor(amo);
       sum=am;
       
       if(am%2!=0){lo=1;loonies.setText(lo+"");to=(am-1)/2;toonies.setText(to+"");}
       else{to=am/2;toonies.setText(to+"");lo=0;loonies.setText(lo+"");}
       
       sum0=sum0-sum;
       sum0=sum0*100;
       
       sum=(int) Math.floor(sum0);
       qua=(int) (sum/25);
       sum=sum-(qua*25);
       
       di=sum/10;
       sum=sum-(di*10);
       
       ni=sum/5;
       sum=sum-(ni*5);
       
       pen=sum;
       
       quarters.setText(qua+"");
       dimes.setText(di+"");
       nickel.setText(ni+"");
       pennies.setText(pen+"");
       
       change.setEnabled(false);
       amount.setEditable(false);
       if(amo>1000){mess.setText("Damn Bouiii!!!! You are Rich!!!"); 
       }
       else if(amo<50){mess.setText("Y!Y!Y wd u use me!!!! ");}
    }//GEN-LAST:event_changeActionPerformed

