    private void selectActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_selectActionPerformed
       String item1;
       item1=(String) itemin.getSelectedItem();
       ITEM.setText("Select the amount of "+item1);
    }//GEN-LAST:event_selectActionPerformed

    private void moneyinActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_moneyinActionPerformed
        float it1,it2,it3,net,tot;
        it1=(float) Double.parseDouble(ITEMIN.getText());
        it2=(float) Double.parseDouble(ITEM2IN.getText());
        it3=(float) Double.parseDouble(ITEM3IN.getText());
        net=(float) ((2.49*it1)+(1.9*it2)+it3);
        tot=(float) (net+(net*0.13));
        OUTPUT.setText("Your bill with taxes is $"+tot);
        money.setText("Enter the amount you gave:");
        
    }//GEN-LAST:event_moneyinActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        float it1,it2,it3,net,tot, back, mny;
        it1=(float) Double.parseDouble(ITEMIN.getText());
        it2=(float) Double.parseDouble(ITEM2IN.getText());
        it3=(float) Double.parseDouble(ITEM3IN.getText());
        net=(float) ((2.49*it1)+(1.9*it2)+it3);
        tot=(float) (net+(net*0.13));
        mny=(float) Double.parseDouble(mny1.getText());
        back=mny-tot;
        jLabel2.setText("Your money back is "+back);
       
    }//GEN-LAST:event_jButton1ActionPerformed

