    private void findActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_findActionPerformed
        float xa,ya,aa,ka,da,ca,xf,yf;
        xa= Float.parseFloat(xini.getText());
        ya=Float.parseFloat(yini.getText());
        aa=Float.parseFloat(a.getText());
        ka=Float.parseFloat(k.getText());
        da=Float.parseFloat(d.getText());
        ca=Float.parseFloat(c.getText());
        xf=(xa*1/ka)-da;
        yf=(ya*aa)+ca;
        xfin.setText(xf+"");
        yfin.setText(yf+"");
    }//GEN-LAST:event_findActionPerformed

